interface CoverProps {
  hasPadding?: boolean;
}

export function Cover({ children, hasPadding = true }: PropsWithChildren<CoverProps>) {
  return (
    <div
      id="frame1"
      style={{
        width: 599,
        background: 'white',
        padding: hasPadding ? 16 : 0,
        display: 'flex',
        flexDirection: 'column',
        gap: 16,
        boxSizing: 'border-box',
      }}
    >
      {children}
    </div>
  );
}

interface ImageProps {
  src: string;
}

export function Image({ src }: ImageProps) {
  return <img style={{ width: '100%' }} src={src} alt="" />;
}

